mapper.select:
select d.id as id, d.name as name, d.price as price, d.image as image, d.description as description, d.status as status, d.create_time as create_time, d.update_time as update_time, c.name as category_name from dish d join category c on c.id = d.category_id
and d.name like concat('%',#{name},'%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.update_time desc
